mapper.update:
UPDATE hall_time
   SET morning = #{morning},
   	  after = #{after},
       eve = #{eve},
       full = #{full}
    WHERE hall_id = #{hall_id}

mapper.select:
select * from equipment
where hall_id = #{id}
order by equip_type

select morning, after, eve, full
from hall_time
where hall_id = #{id}
