mapper.update:
update user
set nickname=#{nickname}
where user_id=#{user_id}

update user
set password=#{password}
where user_id=#{user_id}

update user
set phone=#{phone}
where user_id=#{user_id}

update user
set leave_status = 'Y'
where user_id = #{user_id}

update like_hall
set status = 'N'
where user_id = #{user_id} and hall_id = #{hall_id}

update reservation
set reserve_status = 'N'
where reserve_id = #{reserve_id}

mapper.select:
select equip_name, equip_price, equip_num
from reservation_equipment
where reserve_id = #{reserve_id};

mapper.update:
update reservation
set write_review = 'Y'
where reserve_id = #{reserve_id}
